    const handleSearch = e => {
        setUser({
            ...user,
            [e.target.name]: e.target.value
        })
    }

    let{Nombres,Apellidos,Telefono,Correo} = user

    const handleSubmit = () => {
        //consulta
        const requestInit = {
            method:'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(user)
        }
        fetch('http://localhost:9000/getContactos',requestInit)
        .then(res => res.json())
        .then(res => console.log(res))

        console.log(user)
    }

   

    return (
        <form onSubmit={handleSubmit}>
            <div className="mb-3">
                <label htmlFor="Telefono" className="form-label">Busqueda (Por numero de Telefono)</label>
                <input value={Telefono} name="Telefono" onChange={handleSearch} type="number" id="Telefono" className="form-control"/>
            </div>
            <button type="submit" className="btn btn-primary">Buscar</button>
        </form>
    );
}

export default Search;
