        setUser({
            Nombres:'',
            Apellidos:'',
            Telefono:'',
            Correo:''
        })

    }

    return (
        <form onSubmit={handleSubmit}>
            <div className="mb-3">
                <label htmlFor="Busqueda" className="form-label">Busqueda (Por numero de Telefono)</label>
